import React, { Component } from 'react';
import './App.css';
import Screen from './components/Screen';
import Wrapper from './components/Wrapper';
import iveteimg from "./components/images/ivete.jpg"
import carlinhosimg from "./components/images/carlinhos.jpg"
import Gilbertoimg from "./components/images/Gilberto.jpg"
import mariaimg from "./components/images/maria.jpg"
import Raulimg from "./components/images/Raul.jpg"

function App() {

  let ivete = {
    id: 1,
    name: 'Ivete Sangalo',
    number: vote,
    img: iveteimg
}

let carlinhos = {
    id: 2,
    name: 'Carlinhos Brown',
    number: vote,
    img: carlinhosimg
}

let Gilberto = {
    id: 3,
    name: 'Gilberto Gil',
    number: vote,
    img: Gilbertoimg
}

let maria = {
    id: 4,
    name: 'Maria Bethania',
    number: vote,
    img: mariaimg
}

let Raul = {
    id: 5,
    name: 'Raul Seixas',
    number: vote,
    img: Raulimg
}

let holder = '';

switch (vote) {
    case vote = 72:
            holder = ivete;
        break;
    case vote = 62: 
            holder = carlinhos;
        break;
    case vote = 42: 
            holder = Gilberto;
        break;
    case vote = 46: 
            holder = maria;
        break;
    case vote = 45:
            holder = Raul;
        break;
    default:
            return(
                <div><h1>voto invalido!</h1></div>
            )
}


  return(
      <Wrapper>
        <div className='screen'>
          {Screen(vote)}
        </div>
        <div>
          {}
        </div>
      </Wrapper>
  );
}

export default App;
