import React, { Component, useState } from 'react';
import './App.css';
import Screen from './components/Screen';
import Wrapper from './components/Wrapper';

function App() {
  const [vote, setVote] = useState(0);
  const handleClickBtn = (value) => {
    setVote(Number(vote.toString() + value.toString()))
  }

  const createDigits = () => {
    const digits = [];
    for (let i = 1; i < 10; i++) {
      digits.push(
        <button key={i} onClick={handleClickBtn(i)}>{i}</button>
      )
    }
    return digits;
  }
  return(
      <Wrapper>
        <div className='screen'>
          {Screen(45)}
          {vote}
        </div>
        <div className='btns'>
          {createDigits()}
          <button className='btnC' id='branco'>Branco</button>
          <button className='btnC' id='corrige'>Corrige</button>
          <button className='btnC' id='confirma'>Confirma</button>
        </div>
      </Wrapper>
  );
}

export default App;
