import React, { Component, useContext } from 'react';
import './App.css';
import Screen from './components/Screen';
import Wrapper from './components/Wrapper';
import { ContextV } from './context/Context';

const {vote, setVote} = useContext(ContextV);

function App() {

  const handleClickBtn = () => {
    const stg = vote.toString();

    let numV;
    if(stg === '0' && vote.vote === 0) {
        numV = "0"
    }else{
        numV = Number(vote.vote + stg);
    }

    setVote({
        ...vote,
        num: numV
    })
  }

  const createDigits = () => {
    const digits = [];
    for (let i = 1; i < 10; i++) {
      digits.push(
        <button key={i}>{i}</button>
      )
    }
    return digits;
  }

  return(
      <Wrapper>
        <div className='screen'>
          {Screen(45)}
          {10}
        </div>
        <div className='btns'>
          {createDigits()}
          <button className='btnC' id='branco'>Branco</button>
          <button className='btnC' id='corrige'>Corrige</button>
          <button className='btnC' id='confirma'>Confirma</button>
        </div>
      </Wrapper>
  );
}

export default App;
